@@ -47,3 +47,25 @@
+
+/**
+ * Adds score with combo multiplier based on streak.
+ * Multiplier formula: 1 + Math.floor(streak / 5) * 0.5
+ * Capped at 3.0x maximum.
+ */
+export function addComboScore(state: GameState, basePoints: number, streak: number): GameState {
+  if (state.paused) return state;
+
+  const comboLevel = Math.floor(streak / 5);
+  const rawMultiplier = 1 + comboLevel * 0.5;
+  const multiplier = Math.min(rawMultiplier, 3.0);
+  const finalPoints = Math.round(basePoints * multiplier);
+
+  return {
+    ...state,
+    player: {
+      ...state.player,
+      score: state.player.score + finalPoints,
+      combo: streak,
+    },
+  };
+}

