+export interface GameEvent {
+  type: string;
+  timestamp: number;
+  data?: unknown;
+}
+
+export interface EventLog {
+  events: GameEvent[];
+  maxSize: number;
+}
+
+export function createEventLog(maxSize: number = 50): EventLog {
+  return { events: [], maxSize };
+}
+
+export function logEvent(log: EventLog, event: GameEvent): EventLog {
+  const events = [...log.events, event];
+  if (events.length > log.maxSize) {
+    events.splice(0, events.length - log.maxSize);
+  }
+  return { ...log, events };
+}
+
+export function getRecentEvents(log: EventLog, count: number): GameEvent[] {
+  return log.events.slice(-count);
+}

