+export interface CooldownEntry {
+  name: string;
+  durationTicks: number;
+  remainingTicks: number;
+}
+
+export function createCooldownManager(): CooldownEntry[] {
+  return [];
+}
+
+export function startCooldown(manager: CooldownEntry[], name: string, durationTicks: number): CooldownEntry[] {
+  const existing = manager.findIndex(e => e.name === name);
+  const entry: CooldownEntry = { name, durationTicks, remainingTicks: durationTicks };
+  if (existing >= 0) {
+    return manager.map((e, i) => i === existing ? entry : e);
+  }
+  return [...manager, entry];
+}
+
+export function tickCooldowns(manager: CooldownEntry[]): CooldownEntry[] {
+  return manager.map(e => ({
+    ...e,
+    remainingTicks: Math.max(0, e.remainingTicks - 1),
+  }));
+}
+
+export function isOnCooldown(manager: CooldownEntry[], name: string): boolean {
+  const entry = manager.find(e => e.name === name);
+  return entry ? entry.remainingTicks > 0 : false;
+}
+
+export function getCooldownRemaining(manager: CooldownEntry[], name: string): number {
+  const entry = manager.find(e => e.name === name);
+  return entry ? entry.remainingTicks : 0;
+}

