+export function validateSettings(settings: Partial<GameSettings>): GameSettings {
+  const defaults: GameSettings = {
+    difficulty: 5,
+    soundVolume: 0.8,
+    musicVolume: 0.6,
+    showFps: false,
+  };
+
+  const difficulty = typeof settings.difficulty === 'number'
+    && settings.difficulty >= 1 && settings.difficulty <= 10
+    ? settings.difficulty : defaults.difficulty;
+
+  const soundVolume = typeof settings.soundVolume === 'number'
+    && settings.soundVolume >= 0 && settings.soundVolume <= 1
+    ? settings.soundVolume : defaults.soundVolume;
+
+  const musicVolume = typeof settings.musicVolume === 'number'
+    && settings.musicVolume >= 0 && settings.musicVolume <= 1
+    ? settings.musicVolume : defaults.musicVolume;
+
+  const showFps = typeof settings.showFps === 'boolean'
+    ? settings.showFps : defaults.showFps;
+
+  return { difficulty, soundVolume, musicVolume, showFps };
+}

